mapper.select:
SELECT
    mc.id,
    mc.`name`,
    mc.address,
    mc.qq,
    mc.tel,
    mc.email,
    mc.belongs,
    mc.lasttime,
    mc.time,
    mc.`status`,
    mc.look,
    mc.restatus,
    mc.caim,
    mc.cfrom,
    mc.type,
    mc.keyword,
    mc.etime,
    mc.rate,
    mc.remark,
    mc.count,
    mc.project,
    su.uid,
    su.uname
FROM
    ml_customer AS mc
    LEFT JOIN sys_user AS su ON mc.belongs = su.uid


        and POSITION(#{name} in mc.name)


        and POSITION(#{address} in mc.address)


        and mc.qq=#{qq}


        and POSITION(#{tel} in mc.tel)


        and mc.email=#{email}





                    and mc.belongs=#{belongs}



                and mc.belongs=#{group}






        and mc.lasttime=#{lasttime}


        and mc.time=#{time}


        and mc.`status`=#{status}


        and mc.look=#{look}


        and mc.restatus=#{restatus}


        and mc.caim=#{caim}


        and POSITION(#{cfrom} in mc.cfrom)


        and mc.type=#{type}


        and mc.keyword=#{keyword}


        and mc.etime=#{etime}


        and mc.rate=#{rate}


        and mc.remark=#{remark}


        and mc.count=#{count}


        and mc.project=#{project}


order by mc.time
